function tbx(s){const l=document.querySelector(s);return l.show=()=>{l.style.display="block"},l.hide=()=>{l.style.display="none"},l.addClass=s=>{l.classList.add(s)},l.removeClass=s=>{l.classList.remove(s)},l.toggleClass=s=>{l.classList.toggle(s)},l}tbx.navcollapse=()=>{tbx(".tbx-nav").toggleClass("tbx-collapse-show")};